class Parallax{constructor(t,e){this.bg=document.querySelector(t),this.step=e.step||50,this.zIndex=e.zIndex,this.init()}init(){window.addEventListener("mousemove",t=>{const e=t.clientX/window.innerWidth,n=t.clientY/window.innerHeight;this.bg.style.transform=`translate(-${e*this.step}px, -${n*this.step}px)`})}}const firstParallax=new Parallax(".first-bg",{step:30,zIndex:1}),secondParallax=new Parallax(".second-bg",{step:50,zIndex:2}),thirdParallax=new Parallax(".third-bg",{step:70,zIndex:3});