class Parallax{constructor(t,e){this.bg=document.querySelector(t),this.step=e.step||50,this.zIndex=e.zIndex,this.init()}init(){window.addEventListener("mousemove",t=>{const e=t.clientX/window.innerWidth,s=t.clientY/window.innerHeight;console.log(this),this.bg.style.transform=`translate(-${e*this.step}px, -${s*this.step}px)`,console.log(`x = ${e}, x*step = ${e*this.step}`)})}}const firstParallax=new Parallax(".first-bg",{step:15,zIndex:1}),secondParallax=new Parallax(".second-bg",{step:30,zIndex:2}),thirdParallax=new Parallax(".third-bg",{step:75,zIndex:3}),fourthParallax=new Parallax(".fourth-bg",{step:120,zIndex:4});